import { useState } from "react"
import Botao from "../Botao"
import CampoTexto from "../CampoTexto"
import ListaSuspensa from "../ListaSuspensa"
import "./Formulario.css"

let cursos = [
    "Programação",
    "Front-End",
    "Data Science",
    "Devops",
    "Ux e Design",
    "Mobile",
    "Inovação e Gestão"
]



    const Formulario = () => {

        const [nome, setNome] = useState('Luiz')
        const [cargo, setCargo] = useState('Programador')
        const [imagem, setImagem] = useState('url1')


        const aoSalvar = (evento) => {
            evento.preventDefault()
            console.log(nome, cargo, imagem)
                
        }
        return (
            <section className="formulario">
                <form onSubmit={aoSalvar}>
                    <h1>Preencha os dados para criar o card do colaborador.</h1>
                    <CampoTexto 
                        obrigatorio={true}  
                        label="Nome" 
                        placeholder="Digite o seu nome" 
                        valor={nome}
                        Alterado={valor => setNome(valor)}
                    />
                    <CampoTexto 
                        obrigatorio={true}  
                        label="Cargo" 
                        placeholder="Digite o seu cargo" 
                        valor={cargo}
                        Alterado={valor => setCargo(valor)}
                    />
                    <CampoTexto 
                        obrigatorio={true}  
                        label="Imagem" 
                        placeholder="Informe o endereço da sua imagem"
                        valor={imagem}
                        Alterado={valor => setImagem(valor)}
                    />
                    <ListaSuspensa label="Time" itens={cursos}/>
                    <Botao>Criar Card</Botao>
                </form>
            </section>
        )
    }

export default Formulario
